'use strict';
function add(numbers) {
  let result = 0;
  for (let num of numbers) {
    result = result + num;
  }
  return result;
}
function multi(numbers) {
  let result = 1;
  for (let num of numbers) {
    result = result * num;
  }
  return result;
}
// add 関数は、整数の配列を受け取り、全てを足し合わせる関数
module.exports = {
  add : add,
  multi : multi
};
// なお、この module.exports オブジェクトについて、プロパティの名前と値の変数名が一致する場合、
// module.exports = { add };
// というように省略記法を使うことができます。