//練習
'use strict';
function add(numbers) {
  let result = 0;
  for (let num of numbers) {
    result = result + num;
  }
  return result;
}

function multi(numbers) {//multi：マルチ
  let result = 1;
  for (let num of numbers) {
    result = result * num;
  }
  return result;
}

//console.log(multi([1,2,3,4]));

module.exports = { add, multi };
